const ClimaContext = createContext()


const ClimaProvider = ({children}) => {

  const [busqueda, setBusqueda] = useState({

  })

  return (

        <ClimaContext.Provider
              value={{
                busqueda,
                setBusqueda
              }}
        >
           {children}
        </ClimaContext.Provider>

  )
}

